import React from "react";
import { Bar } from "react-chartjs-2";
import { motion } from "framer-motion";
import "chart.js/auto";
import "./LearningPath.css"; // Custom CSS

const LearningPath = () => {
  // Sample data for the graph (current skills vs. required skills)
  const data = {
    labels: ["JavaScript", "React", "Node.js", "CSS", "Algorithms", "Data Structures"],
    datasets: [
      {
        label: "Current Skill Level",
        data: [4, 3, 2, 3, 1, 2],
        backgroundColor: "rgba(75, 192, 192, 0.6)",
      },
      {
        label: "Required Skill Level",
        data: [8, 7, 6, 7, 6, 7],
        backgroundColor: "rgba(153, 102, 255, 0.6)",
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="learning-path-container">
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
        className="learning-path-intro"
      >
        <h2>Your Personalized Learning Path</h2>
        <p>
          Based on your current skills and desired job role, we've created a
          tailored learning path to help you level up!
        </p>
      </motion.div>

      <div className="learning-path-chart">
        <h3>Skills Breakdown</h3>
        <Bar data={data} options={options} />
      </div>

      <div className="learning-path-steps">
        <h3>Next Steps in Your Journey</h3>
        <motion.ul
          initial={{ x: -200, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.8, type: "spring" }}
        >
          <li>
            <span className="step-number">1</span> Learn advanced JavaScript concepts (promises, async/await).
          </li>
          <li>
            <span className="step-number">2</span> Master React with hooks and state management (Redux, Context API).
          </li>
          <li>
            <span className="step-number">3</span> Build backend skills (Node.js, Express, MongoDB).
          </li>
          <li>
            <span className="step-number">4</span> Dive deeper into algorithms and data structures.
          </li>
          <li>
            <span className="step-number">5</span> Build real-world projects to demonstrate your skills.
          </li>
        </motion.ul>
      </div>
    </div>
  );
};

export default LearningPath;
