import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Registration.css"; // Create this CSS file for styling

const Registration = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ideally, you would validate and send formData to the backend
    console.log("Registration Data:", formData);
    // Redirect to personalized dashboard or next step
    navigate("/courses");
  };

  return (
    <div className="registration-container">
      <h2>Register to Personalize Your Learning Path</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        <button type="submit" className="primary-btn">Get Started</button>
      </form>
    </div>
  );
};

export default Registration;
